-- Daijoubu sqlite3 queries-- DaijoubuJapaneseLanguage.sqlite3;create table tbl_vocabulary_N5(	Id INTEGER PRIMARY KEY AUTOINCREMENT,	furigana varchar(254),	kanji varchar(254),	meaning varchar(254));create table tbl_vocabulary_N4(	Id INTEGER PRIMARY KEY AUTOINCREMENT,	furigana varchar(254),	kanji varchar(254),	meaning varchar(254));create table tbl_grammar_N5(	Id INTEGER PRIMARY KEY AUTOINCREMENT,	sentence_en varchar(254),	sentence_jp varchar(254),	sentence_fu varchar(254));create table tbl_grammar_N4(	Id INTEGER PRIMARY KEY AUTOINCREMENT,	sentence_en varchar(254),	sentence_jp varchar(254),	sentence_fu varchar(254));create table tbl_kana(	Id INTEGER PRIMARY KEY AUTOINCREMENT,	hiragana varchar(9),	katakana varchar(9),	romaji varchar(9));