-- Daijoubu sqlite3 queries
-- DaijoubuJapaneseLanguage.sqlite3;

create table tbl_vocabulary_N5(
	Id INTEGER PRIMARY KEY AUTOINCREMENT,
	furigana varchar(254),
	kanji varchar(254),
	meaning varchar(254)
);

create table tbl_vocabulary_N4(
	Id INTEGER PRIMARY KEY AUTOINCREMENT,
	furigana varchar(254),
	kanji varchar(254),
	meaning varchar(254)
);

create table tbl_grammar_N5(
	Id INTEGER PRIMARY KEY AUTOINCREMENT,
	sentence_en varchar(254),
	sentence_jp varchar(254),
	sentence_fu varchar(254)
);

create table tbl_grammar_N4(
	Id INTEGER PRIMARY KEY AUTOINCREMENT,
	sentence_en varchar(254),
	sentence_jp varchar(254),
	sentence_fu varchar(254)
);

create table tbl_kana(
	Id INTEGER PRIMARY KEY AUTOINCREMENT,
	hiragana varchar(9),
	katakana varchar(9),
	romaji varchar(9)
);

create table tbl_lesson_hiragana(
	Id INTEGER PRIMARY KEY AUTOINCREMENT,
	example varchar(256),
	sound varchar(256),
	mnemonic varchar(256)
);

create table tbl_lesson_katakana(
	Id INTEGER PRIMARY KEY AUTOINCREMENT,
	example varchar(256),
	sound varchar(256),
	mnemonic varchar(256)
);

create table tbl_lesson_introduction(
	Id INTEGER PRIMARY KEY AUTOINCREMENT,
	val1 varchar(256),
	val2 varchar(256),
	val3 varchar(256),
	val4 varchar(256)
);



